    	vec4 newShadowPos = shadowPos;
    	if (normalBias > EPSILON_LOWP)
    	{
    		vec4 viewNormal = matLightView * vec4(worldNormal, 0.0);
    		if (viewNormal.z < 0.1)
    			newShadowPos.xy += viewNormal.xy * projScaleXY * normalBias * clamp(viewNormal.z, 0.001, 0.1);
    	}
    	return newShadowPos;
    }
