    In.position = vec4(a_position, 1.0);
    In.normal = a_normal;
    In.tangent = a_tangent;
  In.position.xy = cc_cameraPos.w == 0.0 ? vec2(In.position.xy.x, -In.position.xy.y) : In.position.xy;
