          #define FXAA_SEARCH_ACCELERATION 2
          #define FXAA_SEARCH_THRESHOLD    (1.0/4.0)
          #define FXAA_SUBPIX              1
          #define FXAA_SUBPIX_FASTER       0
          #define FXAA_SUBPIX_CAP          (3.0/4.0)
          #define FXAA_SUBPIX_TRIM         (1.0/4.0)
