  varying vec2 v_uv;
  uniform sampler2D outputResultMap;
void texcoords(vec2 fragCoord, vec2 resolution,
      out vec2 v_rgbNW, out vec2 v_rgbNE,
      out vec2 v_rgbSW, out vec2 v_rgbSE,
      out vec2 v_rgbM) {
        vec2 inverseVP = 1.0 / resolution.xy;
        v_rgbNW = (fragCoord + vec2(-1.0, -1.0)) * inverseVP;
        v_rgbNE = (fragCoord + vec2(1.0, -1.0)) * inverseVP;
        v_rgbSW = (fragCoord + vec2(-1.0, 1.0)) * inverseVP;
        v_rgbSE = (fragCoord + vec2(1.0, 1.0)) * inverseVP;
        v_rgbM = vec2(fragCoord * inverseVP);
}
void main () {
    #if ANTIALIAS_TYPE == 1
      mediump vec2 v_rgbNW;
      mediump vec2 v_rgbNE;
      mediump vec2 v_rgbSW;
      mediump vec2 v_rgbSE;
      mediump vec2 v_rgbM;
      vec2 resolution = cc_screenSize.xy;
      vec2 fragCoord = v_uv * resolution;
      texcoords(fragCoord, resolution, v_rgbNW, v_rgbNE, v_rgbSW, v_rgbSE, v_rgbM);
      gl_FragColor = fxaa(outputResultMap, fragCoord, resolution, v_rgbNW, v_rgbNE, v_rgbSW, v_rgbSE, v_rgbM);
    #elif ANTIALIAS_TYPE == 2
      vec3 color = FxaaPixelShader(v_uv, outputResultMap, 1.0 / cc_screenSize.xy);
      gl_FragColor = vec4(color, texture2D(outputResultMap, v_uv).a);
    #else
      gl_FragColor = texture2D(outputResultMap, v_uv);
    #endif
  }
